// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Interface that must be implemented to receive entropy callbacks
interface IEntropyCallback {
    function receiveEntropy(bytes32 randomNumber, uint64 sequenceNumber) external;
}

// Interface for requesting entropy from HyperlaneCelo
interface IHyperlaneCelo {
    function requestEntropy() external payable;
    function quoteRequestEntropy() external view returns (uint256);
}

/**
 * @title ExampleEntropyConsumer
 * @notice Example contract showing how to request and receive entropy from HyperlaneCelo
 */
contract ExampleEntropyConsumer is IEntropyCallback {
    // The HyperlaneCelo contract address
    address public hyperlaneCelo;

    // Store the latest entropy received
    bytes32 public lastRandomNumber;
    uint64 public lastSequenceNumber;
    uint256 public entropyRequestCount;

    event EntropyReceived(bytes32 randomNumber, uint64 sequenceNumber);
    event EntropyRequested(uint256 requestNumber);

    constructor(address _hyperlaneCelo) {
        hyperlaneCelo = _hyperlaneCelo;
    }

    /**
     * @notice Request entropy from HyperlaneCelo
     * @dev This function will trigger the cross-chain entropy flow
     */
    function requestRandomness() external payable {
        // Get the quote for how much it costs
        uint256 quote = IHyperlaneCelo(hyperlaneCelo).quoteRequestEntropy();

        // Ensure enough payment was sent
        require(msg.value >= quote, "Insufficient payment");

        // Request the entropy
        IHyperlaneCelo(hyperlaneCelo).requestEntropy{value: quote}();

        entropyRequestCount++;
        emit EntropyRequested(entropyRequestCount);

        // Refund excess payment
        if (msg.value > quote) {
            payable(msg.sender).transfer(msg.value - quote);
        }
    }

    /**
     * @notice Callback function called by HyperlaneCelo when entropy is received
     * @dev This will be called automatically when the entropy arrives
     * @param randomNumber The 32-byte random number from Pyth
     * @param sequenceNumber The Pyth sequence number for this entropy
     */
    function receiveEntropy(bytes32 randomNumber, uint64 sequenceNumber) external override {
        // Only accept callbacks from HyperlaneCelo contract
        require(msg.sender == hyperlaneCelo, "Only HyperlaneCelo can call");

        // Store the entropy
        lastRandomNumber = randomNumber;
        lastSequenceNumber = sequenceNumber;

        emit EntropyReceived(randomNumber, sequenceNumber);

        // TODO: Add your custom logic here
        // For example:
        // - Use the random number for a lottery draw
        // - Generate random NFT traits
        // - Determine game outcomes
        // - etc.
    }

    /**
     * @notice Example: Convert random bytes32 to a number in a range
     * @param max The maximum value (exclusive)
     * @return A random number between 0 and max-1
     */
    function getRandomInRange(uint256 max) public view returns (uint256) {
        require(lastRandomNumber != bytes32(0), "No entropy received yet");
        return uint256(lastRandomNumber) % max;
    }

    /**
     * @notice Example: Generate multiple random numbers from the last entropy
     * @param count How many random numbers to generate
     * @param max The maximum value for each number
     * @return An array of random numbers
     */
    function getMultipleRandom(uint256 count, uint256 max) public view returns (uint256[] memory) {
        require(lastRandomNumber != bytes32(0), "No entropy received yet");
        require(count <= 10, "Max 10 numbers at once");

        uint256[] memory randoms = new uint256[](count);
        bytes32 seed = lastRandomNumber;

        for (uint256 i = 0; i < count; i++) {
            // Hash the seed to get a new random value
            seed = keccak256(abi.encodePacked(seed, i));
            randoms[i] = uint256(seed) % max;
        }

        return randoms;
    }

    /**
     * @notice Allow contract to receive CELO for paying entropy fees
     */
    receive() external payable {}
}

contracts/HyperlaneBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@pythnetwork/entropy-sdk-solidity/IEntropyV2.sol";
import "@pythnetwork/entropy-sdk-solidity/IEntropyConsumer.sol";

// 1. Define the minimal IMailbox interface required to send messages
interface IMailbox {
    function dispatch(
        uint32 _destinationDomain,
        bytes32 _recipientAddress,
        bytes calldata _messageBody
    ) external payable returns (bytes32);

    function quoteDispatch(
        uint32 _destinationDomain,
        bytes32 _recipientAddress,
        bytes calldata _messageBody
    ) external view returns (uint256);
}

contract HyperlaneBase is Ownable, IEntropyConsumer {
    // The Hyperlane Mailbox address on Base Mainnet
    // Source: https://docs.hyperlane.xyz/docs/reference/addresses/deployments/mailbox
    address public constant MAILBOX_ADDRESS = 0xeA87ae93Fa0019a82A727bfd3eBd1cFCa8f64f1D;

    // Pyth Entropy address on Base Mainnet
    // Source: https://docs.pyth.network/entropy/contract-addresses
    address public constant ENTROPY_ADDRESS = 0x6E7D74FA7d5c90FEF9F0512987605a6d546181Bb;

    IMailbox public mailbox;
    IEntropyV2 public entropy;

    // Struct to track pending entropy requests
    struct EntropyRequest {
        uint32 destinationDomain;
        address recipient;
        uint256 length;
        uint256 hyperlaneValue; // ETH sent for Hyperlane gas
        address requester; // The original requester from the origin chain
    }

    // Struct for the entropy data sent cross-chain
    struct EntropyData {
        bytes32 randomNumber;
        uint256 entropyLength;
        uint64 sequenceNumber;
        address sourceContract;
        address requester; // The original requester who should receive the callback
    }

    // Mapping from sequence number to entropy request details
    mapping(uint64 => EntropyRequest) public pendingRequests;

    // Mapping of approved senders: domain => sender address
    mapping(uint32 => address) public approvedSenders;

    // Events to track sent messages
    event MessageSent(bytes32 indexed messageId, uint32 destinationDomain, address recipient);
    event EntropyRequested(uint64 indexed sequenceNumber, uint32 destinationDomain, address recipient, address requester, uint256 length);
    event EntropySent(uint64 indexed sequenceNumber, bytes32 messageId, uint32 destinationDomain, address recipient, bytes32 randomNumber);
    event EntropyRequestReceived(uint32 indexed origin, bytes32 indexed senderContract, address requester);
    event Funded(address indexed funder, uint256 amount);
    event Withdrawn(address indexed recipient, uint256 amount);

    constructor() Ownable(msg.sender) {
        mailbox = IMailbox(MAILBOX_ADDRESS);
        entropy = IEntropyV2(ENTROPY_ADDRESS);
    }

    /**
     * @notice Modifier to ensure only the Mailbox can call the handle function
     */
    modifier onlyMailbox() {
        require(msg.sender == MAILBOX_ADDRESS, "Only Mailbox can call");
        _;
    }

    // Required by IEntropyConsumer interface
    function getEntropy() internal view override returns (address) {
        return address(entropy);
    }

    // Callback function called by Entropy contract with the random number
    function entropyCallback(
        uint64 sequence,
        address, // provider - not used
        bytes32 randomNumber
    ) internal override {
        EntropyRequest memory request = pendingRequests[sequence];
        require(request.destinationDomain != 0, "No pending request for this sequence");

        // Create structured entropy data for clarity on block explorers
        EntropyData memory entropyData = EntropyData({
            randomNumber: randomNumber,
            entropyLength: 32, // bytes32 = 32 bytes
            sequenceNumber: sequence,
            sourceContract: address(this),
            requester: request.requester
        });

        // Convert address to bytes32 for Hyperlane
        bytes32 recipientBytes32 = addressToBytes32(request.recipient);

        // Send the structured entropy data via Hyperlane
        bytes32 messageId = mailbox.dispatch{value: request.hyperlaneValue}(
            request.destinationDomain,
            recipientBytes32,
            abi.encode(entropyData)
        );

        emit EntropySent(sequence, messageId, request.destinationDomain, request.recipient, randomNumber);

        // Clean up the pending request
        delete pendingRequests[sequence];
    }

    /**
     * @notice Handles incoming messages from the Hyperlane Mailbox.
     * @dev This receives entropy requests from the receiver contract on the destination chain.
     *
     * @param _origin The Domain ID of the chain the message came from.
     * @param _sender The address of the sender contract on the origin chain (as bytes32).
     * @param _messageBody The raw byte data sent (the encoded requester address).
     */
    function handle(
        uint32 _origin,
        bytes32 _sender,
        bytes calldata _messageBody
    ) external payable onlyMailbox {
        // Decode the requester address from the message
        address requester = abi.decode(_messageBody, (address));

        // Log both the sender contract (HyperlaneCelo) and the actual requester
        emit EntropyRequestReceived(_origin, _sender, requester);

        // Convert sender bytes32 to address - this is the HyperlaneCelo contract
        address senderContract = bytes32ToAddress(_sender);

        // Trigger entropy request - send entropy back to the sender contract, not the wallet
        _requestAndRelayEntropyInternal(_origin, senderContract, requester, msg.value);
    }

    /**
     * @notice Internal function to request entropy and relay it to a recipient.
     * @param _destinationDomain The Hyperlane Domain ID of the target chain.
     * @param _recipient The address of the contract/wallet receiving the random number.
     * @param _requester The original address that requested the entropy.
     * @param _payment The ETH payment provided for fees.
     */
    function _requestAndRelayEntropyInternal(
        uint32 _destinationDomain,
        address _recipient,
        address _requester,
        uint256 _payment
    ) internal {
        // Calculate required fees
        uint128 entropyFee = entropy.getFeeV2();

        // Estimate Hyperlane cost for sending the entropy data
        bytes32 recipientBytes32 = addressToBytes32(_recipient);
        EntropyData memory dummyData = EntropyData({
            randomNumber: bytes32(0),
            entropyLength: 32,
            sequenceNumber: 0,
            sourceContract: address(this),
            requester: _requester
        });
        bytes memory dummyMessage = abi.encode(dummyData);
        uint256 hyperlaneQuote = mailbox.quoteDispatch(
            _destinationDomain,
            recipientBytes32,
            dummyMessage
        );

        uint256 totalRequired = uint256(entropyFee) + hyperlaneQuote;

        // Check if we have enough funds (from msg.value + contract balance)
        uint256 availableFunds = _payment + address(this).balance;
        require(availableFunds >= totalRequired, "Insufficient funds in contract");

        // Request entropy from Pyth
        uint64 sequenceNumber = entropy.requestV2{value: entropyFee}();

        // Store the request details for the callback
        pendingRequests[sequenceNumber] = EntropyRequest({
            destinationDomain: _destinationDomain,
            recipient: _recipient,
            length: 32,
            hyperlaneValue: hyperlaneQuote,
            requester: _requester
        });

        emit EntropyRequested(sequenceNumber, _destinationDomain, _recipient, _requester, 32);

        // No refund - keep any excess in the contract for future requests
    }

    /**
     * @notice Get a quote for how much it will cost to request and relay entropy.
     * @dev This is a view function - it costs no gas to call.
     *
     * @param _destinationDomain The Hyperlane Domain ID of the target chain.
     * @param _recipient The address of the contract/wallet receiving the random number.
     * @return The total cost in wei (Entropy fee + Hyperlane gas).
     */
    function quoteEntropyRelay(
        uint32 _destinationDomain,
        address _recipient
    ) external view returns (uint256) {
        uint128 entropyFee = entropy.getFeeV2();

        bytes32 recipientBytes32 = addressToBytes32(_recipient);
        bytes memory dummyMessage = abi.encode(bytes32(0));
        uint256 hyperlaneQuote = mailbox.quoteDispatch(
            _destinationDomain,
            recipientBytes32,
            dummyMessage
        );

        return uint256(entropyFee) + hyperlaneQuote;
    }

    /**
     * @notice Request entropy and relay it to a recipient on a destination chain.
     * @dev Owner only. Requires payment for both Entropy fee and Hyperlane gas.
     *      Note: Excess payment is refunded to the contract owner, not msg.sender.
     *
     * @param _destinationDomain The Hyperlane Domain ID of the target chain.
     * @param _recipient The address of the contract/wallet receiving the random number.
     */
    function requestAndRelayEntropy(
        uint32 _destinationDomain,
        address _recipient
    ) external payable onlyOwner {
        _requestAndRelayEntropyInternal(_destinationDomain, _recipient, msg.sender, msg.value);
    }

    /**
     * @notice Get a quote for how much it will cost to send a message.
     * @dev This is a view function - it costs no gas to call.
     *
     * @param _destinationDomain The Hyperlane Domain ID of the target chain (e.g., Celo is 42220).
     * @param _recipient The address of the contract/wallet receiving the message on the target chain.
     * @param _message The string message you want to send.
     * @return The cost in wei (native token) required to send this message.
     */
    function quoteSendMessage(
        uint32 _destinationDomain,
        address _recipient,
        string calldata _message
    ) external view returns (uint256) {
        bytes32 recipientBytes32 = addressToBytes32(_recipient);
        bytes memory messageBytes = bytes(_message);

        return mailbox.quoteDispatch(
            _destinationDomain,
            recipientBytes32,
            messageBytes
        );
    }

    /**
     * @notice Sends a string message to a recipient on a destination chain.
     * @dev The caller must send enough native ETH (on Base) to cover the interchain gas fee.
     *      Only the contract owner can call this function.
     *
     * @param _destinationDomain The Hyperlane Domain ID of the target chain (e.g., Optimism is 10).
     * @param _recipient The address of the contract/wallet receiving the message on the target chain.
     * @param _message The string message to send.
     */
    function sendMessage(
        uint32 _destinationDomain,
        address _recipient,
        string calldata _message
    ) external payable onlyOwner {
        // 3. Convert the address to bytes32 (Hyperlane standard format)
        bytes32 recipientBytes32 = addressToBytes32(_recipient);
        bytes memory messageBytes = bytes(_message);

        // 4. Calculate the required fee (gas payment) for the message
        // This ensures the relayer has funds to deliver the message on the destination chain.
        uint256 quote = mailbox.quoteDispatch(
            _destinationDomain,
            recipientBytes32,
            messageBytes
        );

        // 5. Ensure the user sent enough ETH to cover the quote
        require(msg.value >= quote, "Insufficient payment for interchain gas");

        // 6. Dispatch the message
        // We pass the quoted fee as the 'value' for the call
        bytes32 messageId = mailbox.dispatch{value: quote}(
            _destinationDomain,
            recipientBytes32,
            messageBytes
        );

        emit MessageSent(messageId, _destinationDomain, _recipient);

        // 7. Refund any excess payment to the sender
        if (msg.value > quote) {
            payable(msg.sender).transfer(msg.value - quote);
        }
    }

    /**
     * @notice Receive function to accept ETH for funding entropy requests
     * @dev Allows anyone to send ETH directly to the contract to fund operations
     */
    receive() external payable {
        emit Funded(msg.sender, msg.value);
    }

    /**
     * @notice Get the contract's ETH balance
     * @return The balance in wei
     */
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @notice Emergency withdraw function (owner only)
     * @dev Only use this to recover stuck funds
     * @param _amount The amount of ETH to withdraw in wei
     */
    function emergencyWithdraw(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient balance");
        payable(owner()).transfer(_amount);
        emit Withdrawn(owner(), _amount);
    }

    /**
     * @dev Helper to convert an address to bytes32 (left-padded).
     * Hyperlane requires all recipient addresses to be bytes32.
     */
    function addressToBytes32(address _addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(_addr)));
    }

    /**
     * @dev Helper to convert bytes32 back to an address.
     * Useful for debugging Hyperlane sender addresses.
     */
    function bytes32ToAddress(bytes32 _bytes32) public pure returns (address) {
        return address(uint160(uint256(_bytes32)));
    }
}

contracts/HyperlaneCelo.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

// Interface that contracts must implement to receive entropy callbacks
interface IEntropyCallback {
    /**
     * @notice Called when entropy is received
     * @param randomNumber The 32-byte random number from Pyth
     * @param sequenceNumber The Pyth sequence number for this entropy
     */
    function receiveEntropy(bytes32 randomNumber, uint64 sequenceNumber) external;
}

interface IMailbox {
    function dispatch(
        uint32 _destinationDomain,
        bytes32 _recipientAddress,
        bytes calldata _messageBody
    ) external payable returns (bytes32);

    function quoteDispatch(
        uint32 _destinationDomain,
        bytes32 _recipientAddress,
        bytes calldata _messageBody
    ) external view returns (uint256);

    function addressToBytes32(address _addr) external pure returns (bytes32);
    function bytes32ToAddress(bytes32 _bytes32) external pure returns (address);
}

contract HyperlaneCelo is Ownable {
    // 1. The Mailbox address on the DESTINATION chain (Celo).
    // Source: https://docs.hyperlane.xyz/docs/reference/addresses/deployments/mailbox
    // Celo Chain ID: 42220
    address public constant MAILBOX_ADDRESS = 0x50da3B3907A08a24fe4999F4Dcf337E8dC7954bb; // Celo Mailbox

    IMailbox public mailbox;

    // Source chain configuration (Base is domain 8453)
    uint32 public sourceDomain;
    address public sourceContract;

    // Access control: only this contract can request entropy
    address public allowedRequester;

    // Struct for the entropy data received cross-chain
    struct EntropyData {
        bytes32 randomNumber;
        uint256 entropyLength;
        uint64 sequenceNumber;
        address sourceContract;
        address requester; // The original requester who should receive the callback
    }

    // 2. State variable to store the last received entropy data
    EntropyData public lastEntropyData;
    address public lastSender;
    uint32 public lastOriginDomain;

    event ReceivedMessage(
        uint32 indexed origin,
        address indexed sender,
        bytes32 randomNumber,
        uint256 entropyLength,
        uint64 sequenceNumber,
        address sourceContract,
        address requester
    );

    event EntropyRequested(address indexed requester, bytes32 messageId);
    event EntropyCallbackSent(address indexed requester, bytes32 randomNumber, uint64 sequenceNumber);
    event EntropyCallbackFailed(address indexed requester, bytes reason);
    event AllowedRequesterUpdated(address indexed oldRequester, address indexed newRequester);
    event SourceConfigUpdated(uint32 sourceDomain, address sourceContract);

    constructor(uint32 _sourceDomain, address _sourceContract) Ownable(msg.sender) {
        mailbox = IMailbox(MAILBOX_ADDRESS);
        sourceDomain = _sourceDomain;
        sourceContract = _sourceContract;
    }

    // 3. Modifier to ensure only the Mailbox can call the handle function
    // This prevents malicious users from faking messages by calling handle() directly.
    modifier onlyMailbox() {
        require(msg.sender == MAILBOX_ADDRESS, "Only Mailbox can call");
        _;
    }

    /**
     * @notice Handles an incoming message from the Hyperlane Mailbox.
     * @dev This must match the IMessageRecipient interface signature exactly.
     *
     * @param _origin The Domain ID of the chain the message came from (e.g., Base is 8453).
     * @param _sender The address of the sender contract on the origin chain (as bytes32).
     * @param _messageBody The raw byte data sent (the encoded EntropyData struct).
     */
    function handle(
        uint32 _origin,
        bytes32 _sender,
        bytes calldata _messageBody
    ) external onlyMailbox {
        // 4. Decode the entropy data
        // The sender used `abi.encode(entropyData)`, so we decode it back to the struct
        EntropyData memory entropyData = abi.decode(_messageBody, (EntropyData));

        // Convert bytes32 sender back to address for easier readability
        address senderAddress = bytes32ToAddress(_sender);

        // 5. Application Logic
        // Save the entropy data to state
        lastEntropyData = entropyData;
        lastSender = senderAddress;
        lastOriginDomain = _origin;

        emit ReceivedMessage(
            _origin,
            senderAddress,
            entropyData.randomNumber,
            entropyData.entropyLength,
            entropyData.sequenceNumber,
            entropyData.sourceContract,
            entropyData.requester
        );

        // 6. Callback to the original requester if it's a contract
        // Check if the requester address has code (is a contract)
        if (_isContract(entropyData.requester)) {
            try IEntropyCallback(entropyData.requester).receiveEntropy(
                entropyData.randomNumber,
                entropyData.sequenceNumber
            ) {
                emit EntropyCallbackSent(
                    entropyData.requester,
                    entropyData.randomNumber,
                    entropyData.sequenceNumber
                );
            } catch (bytes memory reason) {
                // Callback failed, but don't revert - just emit event
                emit EntropyCallbackFailed(entropyData.requester, reason);
            }
        }
    }

    /**
     * @notice Request new entropy from the source chain
     * @dev Only callable by the allowed requester contract. Requires payment for Hyperlane gas.
     */
    function requestEntropy() external payable onlyAllowedRequester {
        require(sourceContract != address(0), "Source contract not set");
        require(sourceDomain != 0, "Source domain not set");

        // Convert recipient address to bytes32
        bytes32 recipientBytes32 = addressToBytes32(sourceContract);

        // Create a simple request message (could be expanded to include parameters)
        bytes memory messageBytes = abi.encode(msg.sender);

        // Get quote for sending the message
        uint256 quote = mailbox.quoteDispatch(
            sourceDomain,
            recipientBytes32,
            messageBytes
        );

        require(msg.value >= quote, "Insufficient payment for interchain gas");

        // Send the entropy request to the source chain
        bytes32 messageId = mailbox.dispatch{value: quote}(
            sourceDomain,
            recipientBytes32,
            messageBytes
        );

        emit EntropyRequested(msg.sender, messageId);

        // Refund excess payment
        if (msg.value > quote) {
            payable(msg.sender).transfer(msg.value - quote);
        }
    }

    /**
     * @notice Get a quote for how much it will cost to request entropy
     * @return The cost in wei required to request entropy
     */
    function quoteRequestEntropy() external view returns (uint256) {
        require(sourceContract != address(0), "Source contract not set");
        require(sourceDomain != 0, "Source domain not set");

        bytes32 recipientBytes32 = addressToBytes32(sourceContract);
        bytes memory messageBytes = abi.encode(msg.sender);

        return mailbox.quoteDispatch(
            sourceDomain,
            recipientBytes32,
            messageBytes
        );
    }

    /**
     * @notice Modifier to restrict function access to only the allowed requester contract
     */
    modifier onlyAllowedRequester() {
        require(msg.sender == allowedRequester, "Only allowed requester can call");
        _;
    }

    /**
     * @notice Set the allowed requester contract address (only owner)
     * @param _allowedRequester The address of the contract allowed to request entropy
     */
    function setAllowedRequester(address _allowedRequester) external onlyOwner {
        address oldRequester = allowedRequester;
        allowedRequester = _allowedRequester;
        emit AllowedRequesterUpdated(oldRequester, _allowedRequester);
    }

    /**
     * @notice Update the source chain configuration (only owner)
     * @param _sourceDomain The Hyperlane domain ID of the source chain
     * @param _sourceContract The address of the HyperlaneSource contract
     */
    function setSourceConfig(uint32 _sourceDomain, address _sourceContract) external onlyOwner {
        sourceDomain = _sourceDomain;
        sourceContract = _sourceContract;
        emit SourceConfigUpdated(_sourceDomain, _sourceContract);
    }

    /**
     * @dev Helper to convert bytes32 back to an Ethereum address.
     * Useful because Hyperlane sends the sender address as bytes32.
     */
    function bytes32ToAddress(bytes32 _buf) internal pure returns (address) {
        return address(uint160(uint256(_buf)));
    }

    /**
     * @dev Helper to convert an address to bytes32 (left-padded).
     */
    function addressToBytes32(address _addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(_addr)));
    }

    /**
     * @dev Check if an address is a contract
     */
    function _isContract(address _addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(_addr)
        }
        return size > 0;
    }
}

contracts/TradingCardGame.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";

/**
 * @title TradingCardGame
 * @notice A game where players select prediction cards and earn points based on market outcomes
 * @dev Uses Pyth Network for price feeds and implements difficulty-based scoring
 */
contract TradingCardGame {
    // ============ Constants ============

    uint256 public constant LOBBY_DURATION = 60 seconds; // 60 seconds to join
    uint256 public constant CHOICE_DURATION = 60 seconds; // 1 minute to commit choices
    uint256 public constant RESOLUTION_DURATION = 600 seconds; // 10 minutes for price resolution
    uint256 public constant MIN_PLAYERS = 2; // Minimum players to start game

    // Difficulty-based point values
    uint256 public constant POINTS_PRICE_UP_DOWN = 10;
    uint256 public constant POINTS_PRICE_ABOVE_BELOW = 15;
    uint256 public constant POINTS_MARKET_CAP_VOLUME = 18;
    uint256 public constant POINTS_PERCENTAGE_CHANGE = 20;

    // ============ Enums ============

    enum GameStatus {
        LOBBY, // Waiting for players (60s timer or 2+ players)
        ACTIVE, // Game started, generating cards
        CHOICE, // Players selecting/committing (1 minute)
        RESOLUTION, // Waiting for price resolution (10 minutes)
        ENDED // Game complete, scores calculated
    }

    enum PredictionType {
        PRICE_UP, // 0
        PRICE_DOWN, // 1
        PRICE_ABOVE, // 2
        PRICE_BELOW, // 3
        MARKET_CAP_ABOVE, // 4
        VOLUME_ABOVE, // 5
        PERCENTAGE_CHANGE // 6
    }

    // ============ Structs ============

    struct Game {
        uint256 gameId;
        GameStatus status;
        uint256 startTime;
        uint256 lobbyDeadline;
        uint256 choiceDeadline;
        uint256 resolutionDeadline;
        address[] players;
        uint256[] cards; // Array of 4-digit card numbers
        mapping(address => PlayerChoice) choices;
        mapping(uint256 => PredictionResult) results; // cardNumber => result
        bool resolved;
    }

    struct PlayerChoice {
        address player;
        uint256[3] selectedCards; // 3 card numbers
        uint256 committedAt;
        bool committed;
    }

    struct PredictionResult {
        uint256 cardNumber;
        bool correct;
        uint256 pointsEarned;
    }

    struct PlayerScore {
        address player;
        uint256 totalPoints;
        uint256 gamesPlayed;
        uint256 gamesWon;
    }

    // ============ State Variables ============

    IPyth public immutable pyth;
    uint256 private nextGameId;
    mapping(uint256 => Game) public games;
    mapping(address => PlayerScore) public playerScores;
    address[] public leaderboard; // Top players by total points

    // Track which game each player is currently in (0 means not in any game)
    mapping(address => uint256) public playerActiveGame;

    // Asset to Pyth price ID mapping
    // For simplicity, we'll use a mapping from asset index to price ID
    // Frontend will need to provide the correct price IDs when calling updatePricesAndResolve
    mapping(uint256 => bytes32) public assetPriceIds; // assetIndex => priceId

    // ============ Events ============

    event GameStarted(uint256 indexed gameId, address indexed starter);
    event PlayerJoined(uint256 indexed gameId, address indexed player);
    event GameActive(uint256 indexed gameId, uint256[] cards);
    event ChoicesCommitted(
        uint256 indexed gameId,
        address indexed player,
        uint256[3] cards
    );
    event PricesUpdated(uint256 indexed gameId);
    event GameEnded(uint256 indexed gameId);
    event PointsAwarded(
        uint256 indexed gameId,
        address indexed player,
        uint256 points
    );

    // ============ Constructor ============

    constructor(address _pythAddress) {
        pyth = IPyth(_pythAddress);
        nextGameId = 1;
    }

    // ============ Modifiers ============

    modifier validGame(uint256 _gameId) {
        require(_gameId > 0 && _gameId < nextGameId, "Invalid game ID");
        _;
    }

    modifier gameStatus(uint256 _gameId, GameStatus _status) {
        require(games[_gameId].status == _status, "Invalid game status");
        _;
    }

    // ============ Public Functions ============

    /**
     * @notice Create a new game - anyone can call this
     * @dev Creates a new game in LOBBY state with 60s join window
     * @dev Enforces that a player can only be in one active game at a time
     * @return gameId The ID of the newly created game
     */
    function createGame() external returns (uint256) {
        // Check if player is already in an active game
        uint256 currentGameId = playerActiveGame[msg.sender];
        if (currentGameId > 0) {
            Game storage currentGame = games[currentGameId];
            require(
                currentGame.status == GameStatus.ENDED,
                "Already in an active game"
            );
            // Clear the mapping if the game has ended
            playerActiveGame[msg.sender] = 0;
        }

        uint256 gameId = nextGameId++;
        Game storage game = games[gameId];

        game.gameId = gameId;
        game.status = GameStatus.LOBBY;
        game.startTime = block.timestamp;
        game.lobbyDeadline = block.timestamp + LOBBY_DURATION;
        game.players.push(msg.sender);

        // Track that this player is now in this game
        playerActiveGame[msg.sender] = gameId;

        emit GameStarted(gameId, msg.sender);

        return gameId;
    }

    /**
     * @notice Join an existing game in LOBBY state
     * @param _gameId The game ID to join
     * @dev Enforces that a player can only be in one active game at a time
     */
    function joinGame(
        uint256 _gameId
    ) external validGame(_gameId) gameStatus(_gameId, GameStatus.LOBBY) {
        Game storage game = games[_gameId];
        require(block.timestamp <= game.lobbyDeadline, "Lobby closed");

        // Check if player already joined this game
        for (uint256 i = 0; i < game.players.length; i++) {
            require(game.players[i] != msg.sender, "Already joined");
        }

        // Check if player is already in a different active game
        uint256 currentGameId = playerActiveGame[msg.sender];
        if (currentGameId > 0 && currentGameId != _gameId) {
            Game storage currentGame = games[currentGameId];
            require(
                currentGame.status == GameStatus.ENDED,
                "Already in an active game"
            );
            // Clear the mapping if the game has ended
            playerActiveGame[msg.sender] = 0;
        }

        game.players.push(msg.sender);

        // Track that this player is now in this game
        playerActiveGame[msg.sender] = _gameId;

        emit PlayerJoined(_gameId, msg.sender);
    }

    /**
     * @notice Check if startGame can be called for a game
     * @param _gameId The game ID to check
     * @return canStart True if startGame can be called
     * @return reason Reason why it can't be called (empty if canStart is true)
     */
    function canStartGame(
        uint256 _gameId
    ) external view validGame(_gameId) returns (bool canStart, string memory reason) {
        Game storage game = games[_gameId];

        if (game.status != GameStatus.LOBBY) {
            return (false, "Game is not in LOBBY state");
        }

        if (block.timestamp < game.lobbyDeadline) {
            return (false, "Lobby deadline not reached");
        }

        return (true, "");
    }

    /**
     * @notice Start the game - can be called when timer expires
     * @param _gameId The game ID to start
     * @param _useSecureRandomness If true, uses secure randomness (placeholder for Pyth randomness). If false, uses block-based randomness.
     * @dev Automatically generates cards and transitions to CHOICE phase
     */
    function startGame(
        uint256 _gameId,
        bool _useSecureRandomness
    ) external validGame(_gameId) gameStatus(_gameId, GameStatus.LOBBY) {
        Game storage game = games[_gameId];
        require(
            block.timestamp >= game.lobbyDeadline,
            "Cannot start game yet"
        );

        _startGame(_gameId, _useSecureRandomness);
    }

    /**
     * @notice Generate 10 random 4-digit card numbers for the game (internal only)
     * @param _gameId The game ID
     * @param _useSecureRandomness If true, uses secure randomness (placeholder for Pyth randomness). If false, uses block-based randomness.
     * @dev When _useSecureRandomness is false, uses pseudo-random generation based on block data.
     *      When _useSecureRandomness is true, uses secure randomness (currently static, will be upgraded to Pyth randomness).
     */
    function _generateCards(
        uint256 _gameId,
        bool _useSecureRandomness
    ) internal {
        Game storage game = games[_gameId];
        require(game.cards.length == 0, "Cards already generated");

        // Generate 10 random 4-digit numbers (0000-9999)
        if (_useSecureRandomness) {
            _generateCardsSecure(_gameId);
        } else {
            _generateCardsInsecure(_gameId);
        }
    }

    /**
     * @notice Commit player's 3 selected cards
     * @param _gameId The game ID
     * @param _cardNumbers Array of 3 card numbers (4-digit numbers from game.cards)
     */
    function commitChoices(
        uint256 _gameId,
        uint256[3] memory _cardNumbers
    ) external validGame(_gameId) gameStatus(_gameId, GameStatus.CHOICE) {
        Game storage game = games[_gameId];
        require(
            block.timestamp <= game.choiceDeadline,
            "Choice deadline passed"
        );
        require(!game.choices[msg.sender].committed, "Already committed");

        // Verify player is in the game
        bool isPlayer = false;
        for (uint256 i = 0; i < game.players.length; i++) {
            if (game.players[i] == msg.sender) {
                isPlayer = true;
                break;
            }
        }
        require(isPlayer, "Not a player in this game");

        // Verify card numbers are valid (exist in game.cards)
        for (uint256 i = 0; i < 3; i++) {
            bool cardExists = false;
            for (uint256 j = 0; j < game.cards.length; j++) {
                if (game.cards[j] == _cardNumbers[i]) {
                    cardExists = true;
                    break;
                }
            }
            require(cardExists, "Invalid card number");
        }

        // Store choice
        game.choices[msg.sender] = PlayerChoice({
            player: msg.sender,
            selectedCards: _cardNumbers,
            committedAt: block.timestamp,
            committed: true
        });

        emit ChoicesCommitted(_gameId, msg.sender, _cardNumbers);

        // Check if all players have committed or deadline passed
        // Transition to RESOLUTION phase
        bool allCommitted = true;
        for (uint256 i = 0; i < game.players.length; i++) {
            if (!game.choices[game.players[i]].committed) {
                allCommitted = false;
                break;
            }
        }

        if (allCommitted || block.timestamp >= game.choiceDeadline) {
            game.status = GameStatus.RESOLUTION;
            game.resolutionDeadline = block.timestamp + RESOLUTION_DURATION;
        }
    }

    /**
     * @notice Transition game from CHOICE to RESOLUTION when deadline passes
     * @param _gameId The game ID
     * @dev Can be called by anyone when choice deadline has passed
     *      This ensures games progress even if no one commits
     */
    function transitionToResolution(
        uint256 _gameId
    ) external validGame(_gameId) gameStatus(_gameId, GameStatus.CHOICE) {
        Game storage game = games[_gameId];
        require(
            block.timestamp >= game.choiceDeadline,
            "Choice deadline not reached"
        );

        game.status = GameStatus.RESOLUTION;
        game.resolutionDeadline = block.timestamp + RESOLUTION_DURATION;
    }

    /**
     * @notice Update Pyth prices and resolve predictions
     * @param _gameId The game ID
     * @param _priceUpdateData Array of price update data from Hermes API
     * @param _updateFee Fee to pay for price updates (in wei)
     * @dev This function calls Pyth's updatePriceFeeds, then resolves each prediction
     */
    function updatePricesAndResolve(
        uint256 _gameId,
        bytes[] memory _priceUpdateData,
        bytes32[] memory /* _priceIds */,
        uint256 _updateFee
    )
        external
        payable
        validGame(_gameId)
        gameStatus(_gameId, GameStatus.RESOLUTION)
    {
        Game storage game = games[_gameId];
        require(msg.value >= _updateFee, "Insufficient fee");
        require(!game.resolved, "Already resolved");

        // Update prices on-chain using Pyth
        pyth.updatePriceFeeds{value: _updateFee}(_priceUpdateData);

        emit PricesUpdated(_gameId);

        // Note: Actual price resolution logic would go here
        // For now, we'll mark as resolved. Full resolution happens in endGame
        // This is because we need to compare start prices (at game start) vs end prices (now)
        // We'll need to store start prices when game becomes ACTIVE

        game.resolved = true;
    }

    /**
     * @notice End the game and calculate final scores
     * @param _gameId The game ID
     * @dev Can be called by anyone after resolution deadline. Typically called by keeper bot/cron
     */
    function endGame(uint256 _gameId) external validGame(_gameId) gameStatus(_gameId, GameStatus.RESOLUTION) {
        Game storage game = games[_gameId];
        require(
            block.timestamp >= game.resolutionDeadline,
            "Resolution deadline not reached"
        );

        // Resolve all predictions and calculate scores
        // For each player, check their selected cards and award points
        for (uint256 i = 0; i < game.players.length; i++) {
            address player = game.players[i];
            PlayerChoice storage choice = game.choices[player];

            if (!choice.committed) continue;

            uint256 playerPoints = 0;

            // Check each selected card
            for (uint256 j = 0; j < 3; j++) {
                uint256 cardNumber = choice.selectedCards[j];

                // If not already resolved, resolve it
                if (!game.results[cardNumber].correct) {
                    // TODO: Implement actual price resolution logic
                    // For now, we'll use a placeholder that needs to be implemented
                    // This requires:
                    // 1. Storing start prices when game becomes ACTIVE
                    // 2. Getting current prices from Pyth
                    // 3. Parsing card number to get prediction type and parameters
                    // 4. Checking if prediction is correct
                    // 5. Awarding points based on difficulty

                    // Placeholder: mark as correct for now (needs full implementation)
                    bool isCorrect = _resolvePrediction(_gameId, cardNumber);
                    uint256 points = isCorrect
                        ? _getPointsForCard(cardNumber)
                        : 0;

                    game.results[cardNumber] = PredictionResult({
                        cardNumber: cardNumber,
                        correct: isCorrect,
                        pointsEarned: points
                    });
                }

                playerPoints += game.results[cardNumber].pointsEarned;
            }

            // Update player's total score
            playerScores[player].totalPoints += playerPoints;
            playerScores[player].gamesPlayed++;
            if (playerPoints > 0) {
                playerScores[player].gamesWon++;
            }

            emit PointsAwarded(_gameId, player, playerPoints);
        }

        // Update game status
        game.status = GameStatus.ENDED;

        // Clear player active game mappings for all players in this game
        for (uint256 i = 0; i < game.players.length; i++) {
            if (playerActiveGame[game.players[i]] == _gameId) {
                playerActiveGame[game.players[i]] = 0;
            }
        }

        emit GameEnded(_gameId);
    }

    // ============ View Functions ============

    /**
     * @notice Simple function to verify contract is accessible
     * @return True if contract is working
     */
    function isContractActive() external pure returns (bool) {
        return true;
    }

    /**
     * @notice Diagnostic function to check why endGame might fail
     * @param _gameId The game ID
     * @return canEnd Whether endGame can be called
     * @return reason Reason why it can't be called (empty if canEnd is true)
     * @return gameStatus_ Current game status
     * @return resolutionDeadline_ Resolution deadline timestamp
     * @return currentTime Current block timestamp
     */
    function canEndGame(uint256 _gameId) external view returns (
        bool canEnd,
        string memory reason,
        GameStatus gameStatus_,
        uint256 resolutionDeadline_,
        uint256 currentTime
    ) {
        // Check if game exists
        if (_gameId == 0 || _gameId >= nextGameId) {
            return (false, "Invalid game ID", GameStatus.LOBBY, 0, block.timestamp);
        }

        Game storage game = games[_gameId];
        gameStatus_ = game.status;
        resolutionDeadline_ = game.resolutionDeadline;
        currentTime = block.timestamp;

        // Check game status
        if (game.status != GameStatus.RESOLUTION) {
            return (false, "Game is not in RESOLUTION status", gameStatus_, resolutionDeadline_, currentTime);
        }

        // Check resolution deadline
        if (currentTime < game.resolutionDeadline) {
            return (false, "Resolution deadline not reached", gameStatus_, resolutionDeadline_, currentTime);
        }

        // Check if resolutionDeadline is set (shouldn't be 0)
        if (game.resolutionDeadline == 0) {
            return (false, "Resolution deadline not set (game may not have transitioned properly)", gameStatus_, resolutionDeadline_, currentTime);
        }

        return (true, "", gameStatus_, resolutionDeadline_, currentTime);
    }

    /**
     * @notice Get the next game ID that will be assigned
     * @return The next game ID
     */
    function getNextGameId() external view returns (uint256) {
        return nextGameId;
    }

    /**
     * @notice Get current game state
     * @param _gameId The game ID
     * @return status Game status
     * @return startTime Game start timestamp
     * @return lobbyDeadline Lobby deadline timestamp
     * @return choiceDeadline Choice deadline timestamp
     * @return resolutionDeadline Resolution deadline timestamp
     * @return playerCount Number of players
     * @return cardCount Number of cards generated
     */
    function getGameState(
        uint256 _gameId
    )
        external
        view
        validGame(_gameId)
        returns (
            GameStatus status,
            uint256 startTime,
            uint256 lobbyDeadline,
            uint256 choiceDeadline,
            uint256 resolutionDeadline,
            uint256 playerCount,
            uint256 cardCount
        )
    {
        Game storage game = games[_gameId];
        return (
            game.status,
            game.startTime,
            game.lobbyDeadline,
            game.choiceDeadline,
            game.resolutionDeadline,
            game.players.length,
            game.cards.length
        );
    }

    /**
     * @notice Get game state with player-specific information
     * @param _gameId The game ID
     * @param _player The player address (can be address(0) if not needed)
     * @return status Game status
     * @return startTime Game start time
     * @return lobbyDeadline Lobby deadline timestamp
     * @return choiceDeadline Choice deadline timestamp
     * @return resolutionDeadline Resolution deadline timestamp
     * @return playerCount Number of players in the game
     * @return cardCount Number of cards generated
     * @return playerHasCommitted Whether the specified player has committed their choices
     * @return playerSelectedCards The player's selected cards (empty if not committed or address(0))
     */
    function getGameStateWithPlayer(
        uint256 _gameId,
        address _player
    )
        external
        view
        validGame(_gameId)
        returns (
            GameStatus status,
            uint256 startTime,
            uint256 lobbyDeadline,
            uint256 choiceDeadline,
            uint256 resolutionDeadline,
            uint256 playerCount,
            uint256 cardCount,
            bool playerHasCommitted,
            uint256[3] memory playerSelectedCards
        )
    {
        Game storage game = games[_gameId];

        // Initialize return values
        bool hasCommitted = false;
        uint256[3] memory selectedCards = [uint256(0), uint256(0), uint256(0)];

        // Get player-specific data if player address is provided
        if (_player != address(0)) {
            PlayerChoice storage choice = game.choices[_player];
            if (choice.committed) {
                hasCommitted = true;
                selectedCards = choice.selectedCards;
            }
        }

        // Return all game state data
        return (
            game.status,
            game.startTime,
            game.lobbyDeadline,
            game.choiceDeadline,
            game.resolutionDeadline,
            game.players.length,
            game.cards.length,
            hasCommitted,
            selectedCards
        );
    }

    /**
     * @notice Get all players in a game
     * @param _gameId The game ID
     * @return Array of player addresses
     */
    function getGamePlayers(
        uint256 _gameId
    ) external view validGame(_gameId) returns (address[] memory) {
        return games[_gameId].players;
    }

    /**
     * @notice Get all cards in a game
     * @param _gameId The game ID
     * @return Array of card numbers
     */
    function getGameCards(
        uint256 _gameId
    ) external view validGame(_gameId) returns (uint256[] memory) {
        return games[_gameId].cards;
    }

    /**
     * @notice Get the active game ID for a player
     * @param _player The player address
     * @return gameId The active game ID (0 if no active game)
     * @dev Returns 0 if player has no active game or if their game has ended
     */
    function getPlayerActiveGame(address _player) external view returns (uint256) {
        uint256 gameId = playerActiveGame[_player];
        if (gameId > 0) {
            Game storage game = games[gameId];
            // Only return gameId if the game is still active (not ENDED)
            if (game.status != GameStatus.ENDED) {
                return gameId;
            }
        }
        return 0;
    }

    /**
     * @notice Get player's committed choices
     * @param _gameId The game ID
     * @param _player The player address
     * @return selectedCards Array of 3 card numbers
     * @return committedAt Timestamp when choices were committed
     * @return committed Whether choices were committed
     */
    function getPlayerChoices(
        uint256 _gameId,
        address _player
    )
        external
        view
        validGame(_gameId)
        returns (
            uint256[3] memory selectedCards,
            uint256 committedAt,
            bool committed
        )
    {
        PlayerChoice storage choice = games[_gameId].choices[_player];
        return (choice.selectedCards, choice.committedAt, choice.committed);
    }

    /**
     * @notice Get prediction result for a card
     * @param _gameId The game ID
     * @param _cardNumber The card number
     * @return correct Whether prediction was correct
     * @return pointsEarned Points earned for this card
     */
    function getPredictionResult(
        uint256 _gameId,
        uint256 _cardNumber
    )
        external
        view
        validGame(_gameId)
        returns (bool correct, uint256 pointsEarned)
    {
        PredictionResult storage result = games[_gameId].results[_cardNumber];
        return (result.correct, result.pointsEarned);
    }

    /**
     * @notice Get player's lifetime score
     * @param _player The player address
     * @return totalPoints Total lifetime points
     * @return gamesPlayed Number of games played
     * @return gamesWon Number of games won
     */
    function getPlayerScore(
        address _player
    )
        external
        view
        returns (uint256 totalPoints, uint256 gamesPlayed, uint256 gamesWon)
    {
        PlayerScore storage score = playerScores[_player];
        return (score.totalPoints, score.gamesPlayed, score.gamesWon);
    }

    // ============ Internal Functions ============

    /**
     * @notice Internal function to begin a game
     * @param _gameId The game ID
     */
    function _startGame(uint256 _gameId, bool _useSecureRandomness) internal {
        Game storage game = games[_gameId];
        require(game.status == GameStatus.LOBBY, "Game not in lobby");

        // Generate cards automatically
        _generateCards(_gameId, _useSecureRandomness);

        // Transition directly to CHOICE phase
        game.status = GameStatus.CHOICE;
        game.choiceDeadline = block.timestamp + CHOICE_DURATION;

        emit GameActive(_gameId, game.cards);
    }

    /**
     * @notice Generate cards using insecure block-based randomness
     * @param _gameId The game ID
     * @dev Uses block.timestamp and block.prevrandao for randomness.
     *      This is not cryptographically secure, but sufficient for MVP/testing.
     */
    function _generateCardsInsecure(uint256 _gameId) internal {
        Game storage game = games[_gameId];

        // Generate 10 random 4-digit numbers (0000-9999)
        // Using block.timestamp and block.prevrandao for randomness
        // Note: This is not cryptographically secure, but sufficient for MVP
        for (uint256 i = 0; i < 10; i++) {
            uint256 random = uint256(
                keccak256(
                    abi.encodePacked(
                        block.timestamp,
                        block.prevrandao,
                        _gameId,
                        i,
                        msg.sender
                    )
                )
            ) % 10000;
            game.cards.push(random);
        }
    }

    /**
     * @notice Generate cards using secure randomness (placeholder for Pyth randomness)
     * @param _gameId The game ID
     * @dev Currently returns static values as a placeholder.
     *      Will be upgraded to use Pyth Network's randomness oracle in the future.
     */
    function _generateCardsSecure(uint256 _gameId) internal {
        Game storage game = games[_gameId];

        // TODO: Replace with Pyth Network randomness oracle
        // For now, use static values as a placeholder
        // This ensures the function signature and flow are ready for Pyth integration

        // Static placeholder values (will be replaced with Pyth randomness)
        uint256[10] memory staticCards = [
            uint256(1234),
            uint256(5678),
            uint256(9012),
            uint256(3456),
            uint256(7890),
            uint256(2345),
            uint256(6789),
            uint256(4567),
            uint256(8901),
            uint256(1357)
        ];

        for (uint256 i = 0; i < 10; i++) {
            game.cards.push(staticCards[i]);
        }
    }

    /**
     * @notice Resolve a prediction based on card number and price data
     * @return Whether the prediction was correct
     * @dev This is a placeholder - needs full implementation with price comparison
     */
    function _resolvePrediction(
        uint256 /* _gameId */,
        uint256 /* _cardNumber */
    ) internal pure returns (bool) {
        // TODO: Implement full prediction resolution
        // 1. Parse card number to get asset index, prediction type, etc.
        // 2. Get start price (stored when game became ACTIVE)
        // 3. Get end price (from Pyth, already updated)
        // 4. Check prediction based on type:
        //    - PRICE_UP: endPrice > startPrice
        //    - PRICE_DOWN: endPrice < startPrice
        //    - PRICE_ABOVE: endPrice > startPrice * (1 + targetValue)
        //    - PRICE_BELOW: endPrice < startPrice * (1 - targetValue)
        //    - etc.

        // Placeholder: return false for now
        // This needs to be fully implemented with actual price data
        return false;
    }

    /**
     * @notice Get points for a card based on its prediction type
     * @param _cardNumber The 4-digit card number
     * @return Points value for this card
     */
    function _getPointsForCard(
        uint256 _cardNumber
    ) internal pure returns (uint256) {
        // Parse card number to get prediction type
        // cardNumber format: ABCD where:
        // A = target index (0-9)
        // B = direction index (0-9)
        // C = prediction type index (0-9) -> maps to PredictionType enum
        // D = asset index (0-9)

        uint256 predictionTypeIndex = (_cardNumber / 10) % 10;

        // Map to PredictionType enum (0-6)
        PredictionType predType = PredictionType(predictionTypeIndex % 7);

        // Return points based on difficulty
        if (
            predType == PredictionType.PRICE_UP ||
            predType == PredictionType.PRICE_DOWN
        ) {
            return POINTS_PRICE_UP_DOWN;
        } else if (
            predType == PredictionType.PRICE_ABOVE ||
            predType == PredictionType.PRICE_BELOW
        ) {
            return POINTS_PRICE_ABOVE_BELOW;
        } else if (
            predType == PredictionType.MARKET_CAP_ABOVE ||
            predType == PredictionType.VOLUME_ABOVE
        ) {
            return POINTS_MARKET_CAP_VOLUME;
        } else if (predType == PredictionType.PERCENTAGE_CHANGE) {
            return POINTS_PERCENTAGE_CHANGE;
        }

        return 0;
    }

    // ============ Admin Functions ============

    /**
     * @notice Set Pyth price ID for an asset index
     * @param _assetIndex The asset index (0-9)
     * @param _priceId The Pyth price ID (bytes32)
     * @dev This allows configuring which Pyth price feed to use for each asset
     */
    function setAssetPriceId(uint256 _assetIndex, bytes32 _priceId) external {
        // TODO: Add access control (onlyOwner or similar)
        assetPriceIds[_assetIndex] = _priceId;
    }
}

